CREATE TABLE IF NOT EXISTS `namespace` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT, `name` VARCHAR(128) NOT NULL, `alias` VARCHAR(128) NOT NULL, `meta` BLOB NOT NULL, `dhtracks` VARCHAR(128) NOT NULL, `enabled` BINARY(1) NOT NULL DEFAULT 1, `createdtime` TIMESTAMP(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6), `createdby` VARBINARY(128) NOT NULL DEFAULT '', `updatedtime` TIMESTAMP(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6) ON UPDATE CURRENT_TIMESTAMP(6), `updatedby` VARBINARY(128) NOT NULL DEFAULT '', PRIMARY KEY (`id`), UNIQUE INDEX `uq_namespace_name` (`name`), UNIQUE INDEX `uq_namespace_alias` (`alias`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS `namespace_member` ( `namespaceid` INT UNSIGNED NOT NULL, `userid` VARCHAR(128) NOT NULL, PRIMARY KEY (`namespaceid`, `userid`), CONSTRAINT `fk_namespace_member_namespace` FOREIGN KEY (`namespaceid`) REFERENCES `namespace` (`id`), CONSTRAINT `fk_namespace_member_user` FOREIGN KEY (`userid`) REFERENCES `user` (`userid`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS `namespace_follower` ( `namespaceid` INT UNSIGNED NOT NULL, `userid` VARCHAR(128) NOT NULL, PRIMARY KEY (`namespaceid`, `userid`), CONSTRAINT `fk_namespace_follower_namespace` FOREIGN KEY (`namespaceid`) REFERENCES `namespace` (`id`), CONSTRAINT `fk_namespace_follower_user` FOREIGN KEY (`userid`) REFERENCES `user` (`userid`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS `content` ( `sha2` BINARY(32) NOT NULL, `data` BLOB NOT NULL, `createdtime` TIMESTAMP(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6), `createdby` VARBINARY(128) NOT NULL DEFAULT '', PRIMARY KEY (`sha2`)) ENGINE = InnoDB;
CREATE TABLE IF NOT EXISTS `folder` ( `id` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT, `name` VARCHAR(512) NOT NULL, `type` TINYINT UNSIGNED NOT NULL COMMENT 'refers to a meta table.

Possible values are 
- Dashboard
- Alert
', `path` VARCHAR(512) NOT NULL, `pathhash` BINARY(16) NOT NULL, `parentpathhash` BINARY(16) NULL, `contentid` BINARY(32), `createdtime` TIMESTAMP(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6), `createdby` VARBINARY(128) NOT NULL DEFAULT '', `updatedtime` TIMESTAMP(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6) ON UPDATE CURRENT_TIMESTAMP(6), `updatedby` VARBINARY(128) NOT NULL DEFAULT '', PRIMARY KEY (`id`), UNIQUE INDEX `uq_folder_type_pathhash` (`type`, `pathhash`), INDEX `idx_folder_parentpathhash` (`parentpathhash`), CONSTRAINT `fk_folder_content` FOREIGN KEY (`contentid`) REFERENCES `content` (`sha2`)) ENGINE = InnoDB COMMENT = 'Need to partition table by type.';
